try {
  const output = $node["AI Content Repurposer"].json.output;
  // Attempt to parse if it's a string, or just return if it's already an object
  return typeof output === 'string' ? JSON.parse(output) : output;
} catch (e) {
  return {
    error: "Failed to parse AI output as JSON",
    raw: $node["AI Content Repurposer"].json.output
  };
}
